import React, { useState } from 'react';

function GrievanceForm() {
  // State untuk menyimpan data formulir
  const [formData, setFormData] = useState({
    title: '',
    category: '',
    date: '',
    location: '',
    reportContent: '',
    image: null,
  });

  // Fungsi untuk mengubah nilai state saat input berubah
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Fungsi untuk menangani perubahan file gambar
  const handleImageChange = (event) => {
    setFormData({
      ...formData,
      image: event.target.files[0],
    });
  };

  // Fungsi yang akan dijalankan saat formulir dikirim
  const handleSubmit = (event) => {
    event.preventDefault();
    // Di sini Anda akan mengirim data formData ke server menggunakan fetch atau library lain
    console.log(formData); // Untuk sementara, kita hanya akan mencetak data ke console
  };

  return (
    <div className="grievance-form">
      <h2>Formulir Pengaduan</h2>
      <p>Harap periksa kembali sebelum mengirimkan!</p>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Judul</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
          />
        </div>
        {/* Elemen input lainnya (kategori, tanggal, lokasi, dll.) */}
        <div>
          <label htmlFor="reportContent">Isi Laporan</label>
          <textarea
            id="reportContent"
            name="reportContent"
            value={formData.reportContent}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
          />
        </div>
        <div>
          <label htmlFor="image">Unggah Gambar</label>
          <input
            type="file"
            id="image"
            name="image"
            onChange={handleImageChange}
          />
        </div>
        <button type="submit">Kirim</button>
      </form>
    </div>
  );
}

export default GrievanceForm;
