import React from 'react';

const Statistics = () => {
  const sectionStyle = {
    background: 'linear-gradient(to bottom, #e8f5e9, #ffffff)',
    padding: '50px 20px',
    borderRadius: '10px',
    marginBottom: '20px',
    textAlign: 'center',
  };

  const headingStyle = {
    color: '#388e3c',
    fontSize: '28px',
    marginBottom: '20px',
  };

  const textStyle = {
    color: '#555',
    fontSize: '18px',
    marginBottom: '20px',
  };

  const cardContainerStyle = {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    flexWrap: 'wrap',
  };

  const cardStyle = {
    backgroundColor: '#f1f8e9',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 2px 5px rgba(0, 0, 0, 0.1)',
    width: '300px',
    textAlign: 'center',
  };

  return (
    <section style={sectionStyle}>
      <h1 style={headingStyle}>Environmental Statistics</h1>
      <p style={textStyle}>
        Here are some real-time statistics on the environmental problems reported by users.
      </p>
      <div style={cardContainerStyle}>
        <div style={cardStyle}>
          <h3 style={{ color: '#388e3c' }}>Air Pollution</h3>
          <p>Reported Issues: 120</p>
        </div>
        <div style={cardStyle}>
          <h3 style={{ color: '#388e3c' }}>Waste Management</h3>
          <p>Reported Issues: 85</p>
        </div>
        <div style={cardStyle}>
          <h3 style={{ color: '#388e3c' }}>Water Pollution</h3>
          <p>Reported Issues: 65</p>
        </div>
      </div>
    </section>
  );
};

export default Statistics;
